import styled from "styled-components";
import FormikTextField from "../../utils/FormikTextField";
import Button from "@mui/material/Button";
import { Form } from "formik";

export const SignUpImage = styled.div`
  background: url("https://images.unsplash.com/photo-1634370058578-41e7092f261f?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=687&q=80")
    no-repeat center center/cover;
  height: 60vh;
  width: 100%;
  border-radius: 6rem 0 0 6rem;
`;

export const TextField = styled(FormikTextField)`
  width: 100%;
  margin: 3rem;
`;

export const MuiBtn = styled(Button)`
  &&&&&&&&&&&& {
    width: 80%;
    background-color: ${({ bgColor }) => bgColor || ""};
    &:focus,
    &:hover,
    &:visited,
    &:link,
    &:active {
      background-color: "#F3A183";
    }
  }
`;

export const SignBtn = styled.button`
  box-shadow: 0px 3px 1px -2px rgb(0 0 0 / 20%),
    0px 2px 2px 0px rgb(0 0 0 / 14%), 0px 1px 5px 0px rgb(0 0 0 / 12%);
  color: ${({ color }) => color || "#fff"};
  cursor: pointer;
  border-radius: ${({ br }) => br || ".6rem"};
  background-color: ${({ bgColor }) => bgColor || "transparent"};
  font-weight: 500;
  font-size: 0.875rem;
  line-height: 1.75;
  letter-spacing: 0.02857em;
  text-transform: uppercase;
  min-width: 64px;
  padding: 6px 16px;
  border-radius: 4px;
  user-select: none;
  vertical-align: middle;
  outline: 0;
  border: 0;
  margin: 0;
  display: inline-flex;
  -webkit-box-align: center;
  align-items: center;
  -webkit-box-pack: center;
  justify-content: center;
  position: relative;
  box-sizing: border-box;
  -webkit-tap-highlight-color: transparent;
  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms,
    box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms,
    border-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms,
    color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
`;

export const StyledForm = styled(Form)`
  height: 65%;
  width: 80%;
`;
