import styled from "styled-components";

export const StyledFooter = styled.footer`
  left: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  background-color: ${({ theme }) => theme.bg.footer};
  text-align: center;
  margin-left: auto;
`;

export const Wrapper = styled.div``;
