import * as yup from "yup";
export const registerationSchema = yup.object({
  firstName: yup.string().min(2, "First name is too short!").required("First name is required!"),
  lastName: yup.string().min(2, "Last name is too short!").required("Last name is required!"),
  email: yup.string().lowercase().email("Email is not valid").required("Email is required!"),
  password: yup.string().matches(/^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,}$/, "Password be at least 8 characters longs, contains at least one lowercase charater, one uppercase character, one digit and one special character").required("Password is required!"),
  passwordConfirm: yup.string().oneOf([yup.ref("password")], "Passwords must match").required("Passwords do not match.")
});
export const loginSchema = yup.object({
  email: yup.string().lowercase().email("Email is not valid").required("Email is required!"),
  password: yup.string().required("Password is required!")
});
