import styled from "styled-components";

export const Container = styled.div`
  margin: 0 auto;
`;

export const Btn = styled.button`
  color: #fff;
  cursor: pointer;
  display: block;
  position: relative;
  border: 2px solid #f7ca18;
  transition: all 0.4s cubic-bezier(0.215, 0.61, 0.355, 1) 0s;

  &:hover {
    color: #000 !important;
    background-color: transparent;
    text-shadow: none;
    &:before {
      bottom: 0%;
      top: auto;
      height: 100%;
    }
  }

  &:before {
    display: block;
    position: absolute;
    left: 0px;
    top: 0px;
    height: 0px;
    width: 100%;
    z-index: -1;
    content: "";
    color: #000 !important;
    background: #f7ca18;
    transition: all 0.4s cubic-bezier(0.215, 0.61, 0.355, 1) 0s;
  }
`;
