import styled from "styled-components";

export const LandingStyle = styled.div``;

export const LandingImage = styled.div`
  background-color: ${({ theme }) => theme.bg.landing};
  width: 100%;
  height: 100%;
  overflow: hidden;
  background: -webkit-linear-gradient(
      rgba(29, 38, 113, 0.8),
      rgba(195, 55, 100, 0.8)
    ),
    url("https://images.unsplash.com/photo-1506564461966-4107c88f6d29?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1129&q=80")
      no-repeat center center/cover;
  background: linear-gradient(rgba(29, 38, 113, 0.8), rgba(195, 55, 100, 0.8)),
    url("https://images.unsplash.com/photo-1506564461966-4107c88f6d29?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1129&q=80")
      no-repeat center center/cover;
  opacity: 0.8;
`;

export const LandingInner = styled.div`
  height: 100%;
  width: 100%;
  padding-bottom: 10rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  color: ${({ theme }) => theme.colors.white};
  font-size: 7rem;
`;

export const DarkOverlay = styled.div`
  background-color: rgba(0, 0, 0, 1);
  position: absolute;
  width: 100%;
  height: 40rem;
`;
