import styled from "styled-components";

export const StyledFooter = styled.div`
  left: 0;
  bottom: 0;
  width: 100%;
  background-color: ${({ theme }) => theme.footer};
  color: #ff0000;
  text-align: center;
  margin-left: auto;
  position: fixed;
`;

export const Wrapper = styled.div``;
