import styled from "styled-components";

export const Container = styled.div`
  margin: 0 auto;
  border: none;
  outline: 0;
`;
export const Btn = styled.button`
  color: ${({ color }) => color || "#fff"};
  cursor: pointer;
  border-radius: ${({ br }) => br || ".6rem"};
  background-color: ${({ bgColor }) => bgColor || "transparent"};
  font-weight: 400;
  font-size: ${({ size }) => size || "1.2rem"};
  line-height: 1;
  padding: ${({ padding }) => padding || "0.7rem 1.3rem"};
  text-decoration: none;
  text-align: center;
  text-transform: uppercase;
  margin: ${({ margin }) => margin || 0};
  border: ${({ border }) => border || "none"};
  &:hover {
    box-shadow: 0 0 40px 40px
      ${({ boxShadowColor }) => boxShadowColor || "rgba(238, 238, 238, 0.1)"}
      inset;
    color: ${({ hoverColor }) => hoverColor || "#fff"};
  }
  -webkit-transition: box-shadow 300ms ease-in-out, color 300ms ease-in-out;
  transition: box-shadow 300ms ease-in-out, color 300ms ease-in-out;
`;

export const Divider = styled.hr`
  margin: 1rem 1rem;
  width: 30%;
  border-top: 3rem dotted #bbb;
  outline: 0;
  overflow: hidden;
`;
