import styled from "styled-components";

export const LandingImage = styled.div`
  background-color: ${({ theme }) => theme.bg.landing};
  width: 100%;
  height: 60rem;
  overflow: hidden;
  background: -webkit-linear-gradient(
      rgba(29, 38, 113, 0.8),
      rgba(195, 55, 100, 0.8)
    ),
    url("https://images.unsplash.com/photo-1506564461966-4107c88f6d29?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1129&q=80")
      no-repeat center center/cover;
  background: linear-gradient(rgba(0, 0, 0, 0.2), rgba(67, 67, 67, 0.2)),
    url("https://images.pexels.com/photos/4072840/pexels-photo-4072840.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260")
      no-repeat center center/cover;
`;

export const LandingInner = styled.div`
  height: 100%;
  width: 100%;
  text-align: center;
  color: ${({ theme }) => theme.colors.white};
  margin-top: -1rem;
`;

export const Title = styled.div`
  @import url("https://fonts.googleapis.com/css2?family=Ephesis&display=swap");
  font-family: "Ephesis", cursive;
  font-size: ${({ fontSize }) => fontSize || "8rem"};
  margin: 3.5rem 0;
`;

export const Subtitle = styled.div`
  margin-top: 1.3rem;
  font-size: 1.5rem;
`;

export const Features = styled.div`
  height: 30rem;
  width: 100%;
  background-color: ${({ theme }) => theme.bg.features};
`;
