import styled from "styled-components";
import { Link } from "react-router-dom";

export const Container = styled.div`
  margin: ${({ m }) => m || "0 auto"};
  min-height: ${({ mh }) => mh || ""};
  position: relative;
`;

// Styled button
export const Btn = styled.button`
  color: ${({ color }) => color || "#fff"};
  cursor: pointer;
  border-radius: ${({ br }) => br || ".6rem"};
  background-color: ${({ bgColor }) => bgColor || "transparent"};
  font-weight: 400;
  font-size: ${({ size }) => size || "1.2rem"};
  line-height: 1;
  padding: ${({ padding }) => padding || "0.7rem 1.3rem"};
  text-decoration: none;
  text-align: center;
  text-transform: uppercase;
  margin: ${({ margin }) => margin || 0};
  border: ${({ border }) => border || "none"};
  &:hover {
    box-shadow: 0 0 40px 40px
      ${({ boxShadowColor }) => boxShadowColor || "rgba(238, 238, 238, 0.1)"}
      inset;
    color: ${({ hoverColor }) => hoverColor || "#fff"};
  }
  -webkit-transition: box-shadow 300ms ease-in-out, color 300ms ease-in-out;
  transition: box-shadow 300ms ease-in-out, color 300ms ease-in-out;
`;

// Section divider
export const Divider = styled.div`
  @import url("https://fonts.googleapis.com/css2?family=Festive&display=swap");
  text-align: center;
  font-size: 7rem;
  margin-bottom: 3.5rem;
  color: #bdbdbd;
  font-family: "Festive", cursive;
  &::after {
    content: "...";
    font-size: 7rem;
    color: #bdbdbd;
    display: block;
    text-align: center;
    letter-spacing: 0.5rem;
  }
`;

// Flex containers
export const FlexCentered = styled.div`
  height: 100%;
  width: ${({ w }) => w || "100%"};
  flex-direction: column;
  justify-content: center;
  align-items: center;
  display: flex;
  margin: ${({ margin }) => margin || ""};
`;

export const Flex = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  justify-content: ${({ jc }) => jc || "flex-start"};
  align-items: ${({ ai }) => ai || "flex-start"};
`;

export const FlexItem = styled.div`
  flex-grow: ${({ fg }) => fg || ""};
`;

// React router link
export const StyledLink = styled(Link)`
  text-decoration: none;
`;
