import styled from "styled-components";

export const Container = styled.div`
  margin: 0 auto;
`;

export const Btn = styled.button`
  color: #e74c3c;
  cursor: pointer;
  border: 2px solid #e74c3c;
  border-radius: 0.6em;
  background-color: transparent;
  font-weight: 400;
  line-height: 1;
  margin: 20px;
  padding: 1.2em 2.8em;
  text-decoration: none;
  text-align: center;
  text-transform: uppercase;
  &:hover,
  &:focus {
    color: #fff;
    outline: 0;
  }
  &:hover {
    box-shadow: 0 0 40px 40px #e74c3c inset;
  }
  -webkit-transition: box-shadow 300ms ease-in-out, #a1887f 300ms ease-in-out;
  transition: box-shadow 300ms ease-in-out, #a1887f 300ms ease-in-out;
`;
