import styled from "styled-components";

export const Container = styled.div`
  margin: 0 auto;
`;

export const LandingImage = styled.div`
  background: url("https://images.unsplash.com/photo-1506564461966-4107c88f6d29?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1129&q=80")
    no-repeat center center/cover;
  background-color: ${({ theme }) => theme.bg.landing};
  width: 100%;
  height: 100%;
`;

export const LandingInner = styled.div`
  height: 100%;
  width: 100%;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
`;

export const DarkOverlay = styled.div`
  background-color: rgba(0, 0, 0, 1);
  position: absolute;
  width: 100%;
  height: 40rem;
`;
